    private void BoIgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BoIgualActionPerformed
        TePost.setText("");
        TeRes.setText("");
        EtError.setText("");
        try {
            //fase1
            Stack<String> pila = new Stack<String>();
            LinkedList<String> cola = new LinkedList<String>();
            pila.push("(");
            Scanner miEscaner = new Scanner(TeExp.getText() + " )");
            while (!pila.empty()) {
                if (miEscaner.hasNext()) {
                    String aux = miEscaner.next();
                    if (esNumero(aux)) {
                        cola.add(aux);
                    }
                    else if (aux.equals("(")) {
                        pila.push(aux);
                    }
                    else if (esOperador(aux)) {
                        while (esOperador(pila.peek()) && 
                                            masPrecedente(pila.peek(), aux)) {
                            cola.add(pila.pop());
                        }
                        pila.push(aux);
                    }
                    else if (aux.equals(")")) {
                        while (!pila.peek().equals("(")) {
                            cola.add(pila.pop());
                        }
                        pila.pop();
                    } else {
                        throw new Exception();
                    }
                } else {
                    throw new Exception();
                }
            }
            //fase2
            Stack<Double> pila2 = new Stack<Double>();
            while(!cola.isEmpty()){
                TePost.setText(TePost.getText()+cola.element()+" ");
                if (esNumero(cola.element())) {
                    pila2.push(Double.parseDouble(cola.remove()));
                    TeRes.setText(TeRes.getText()+pila2.peek()+" ");
                } else {
                    double y = pila2.pop();
                    double x = pila2.pop();
                    pila2.push(operar(cola.remove(), x, y));
                    TeRes.setText(TeRes.getText()+pila2.peek()+" ");
                }
            }
            TeRes.setText(pila2.peek().toString());
        } catch (Exception e) {
            EtError.setText("Expresi√≥n mal formada");
            TePost.setText("");
            TeRes.setText("");
        }
        
 
    }//GEN-LAST:event_BoIgualActionPerformed

